import React from "react";
import ExerciseCard from "./ExerciseCard"; // Make sure this accepts movement_name!

export default function GroupedExercises({ groupedData }) {
  return (
    <div className="grouped-container">
      {groupedData.map((group) => (
        <div key={group.routine_id} className="group">
          <h2 className="group-title">{group.routine_name}</h2>
          <p style={{ marginBottom: "1rem", color: "#555" }}>
            {group.routine_description}
          </p>
          <div className="exercise-list">
            {group.exercises.map((exercise, idx) => (
              <ExerciseCard
                key={idx}
                name={exercise.movement_name || `Exercise #${exercise.exercise_id}`}
                sets={exercise.sets}
                reps={exercise.reps}
                time={exercise.time}
                rest={exercise.rest}
                instructions={exercise.instructions}
              />
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
