// movementsService.js
import { supabase } from "../supabaseClient";

export async function getAllMovements() {
  const { data, error } = await supabase
    .from("movement")
    .select(`
      id,
      name,
      type,
      equipment,
      movement_muscle (
        role,
        muscle ( id, name, muscle_group )
      )
    `);
  if (error) throw error;
  return data;
}
