import React, { useState, useEffect } from "react";
import NavBar from "../components/NavBar.tsx";
import FlipCard from "../components/Flipcard.tsx";
import {
  getAllMovements,
  getAllMuscles,
  getRoutineNums,
} from "../components/movementService.js";
import "./Encyclopedia.css";

export default function CreateWorkoutFlow() {
  // --- State to track current step ---
  const [step, setStep] = useState(1);

  // --- Workout info ---
  const [workoutData, setWorkoutData] = useState({
    id: null,
    name: "",
    description: "",
  });

  // --- Exercises for step 2 ---
  const [exercises, setExercises] = useState([]);

  // --- Movements and Muscles data for encyclopedias ---
  const [movements, setMovements] = useState([]);
  const [muscles, setMuscles] = useState([]);
  const [muscleGroups, setMuscleGroups] = useState({});

  // --- Filters and search queries for encyclopedias ---
  const [movementQuery, setMovementQuery] = useState("");
  const [muscleQuery, setMuscleQuery] = useState("");
  const [filters, setFilters] = useState({
    workout: true,
    stretch: true,
    trigger_point: true,
    muscles: false,
  });
  const [muscleGroupFilters, setMuscleGroupFilters] = useState({
    arm: true,
    back: true,
    chest: true,
    core: true,
    etc: true,
    foot: true,
    hand: true,
    leg: true,
    neck: true,
    shoulder: true,
  });

  // --- Generate new routine ID on mount ---
  useEffect(() => {
    async function fetchRoutineIds() {
      try {
        const nums = await getRoutineNums();
        // Find max and +1 or fallback to 1
        const newId = nums.length ? Math.max(...nums) + 1 : 1;
        setWorkoutData((prev) => ({ ...prev, id: newId }));
      } catch (err) {
        console.error("Failed to get routine IDs", err);
      }
    }
    fetchRoutineIds();
  }, []);

  // --- Fetch movements and muscles for encyclopedias ---
  useEffect(() => {
    getAllMovements()
      .then(setMovements)
      .catch(console.error);

    getAllMuscles()
      .then((data) => {
        setMuscles(data);
        // Build muscleGroups map by name
        const mg = data.reduce((acc, m) => {
          acc[m.name] = m.muscle_group;
          return acc;
        }, {});
        setMuscleGroups(mg);
      })
      .catch(console.error);
  }, []);

  // --- Step 1: Workout details form ---
  const Step1 = ({ initialData, onNext }) => {
    const [name, setName] = useState(initialData.name || "");
    const [description, setDescription] = useState(initialData.description || "");

    const handleSubmit = (e) => {
      e.preventDefault();
      if (!name.trim()) {
        alert("Please enter a workout name.");
        return;
      }
      onNext({ id: initialData.id, name: name.trim(), description: description.trim() });
    };

    return (
      <form
        onSubmit={handleSubmit}
        style={{
          maxWidth: "600px",
          margin: "2rem auto",
          padding: "1rem",
          border: "1px solid #ccc",
          borderRadius: "10px",
          backgroundColor: "white",
        }}
      >
        <h2>Step 1: Workout Details</h2>
        <p>
          Generated Workout ID: <strong>{initialData.id ?? "Loading..."}</strong>
        </p>

        <label style={{ display: "block", marginBottom: "1rem" }}>
          Workout Name:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{ width: "100%", padding: "0.5rem", marginTop: "0.25rem" }}
            required
          />
        </label>

        <label style={{ display: "block", marginBottom: "1rem" }}>
          Description:
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{ width: "100%", padding: "0.5rem", marginTop: "0.25rem", minHeight: "100px" }}
          />
        </label>

        <button
          type="submit"
          style={{
            padding: "0.75rem 1.5rem",
            backgroundColor: "#28a745",
            color: "white",
            border: "none",
            borderRadius: "8px",
            fontSize: "1.1rem",
            cursor: "pointer",
          }}
        >
          Next →
        </button>
      </form>
    );
  };

  // --- Step 2: Add exercises one by one ---
  const Step2 = ({ routineId, onBack, onFinish }) => {
    const [currentExercise, setCurrentExercise] = useState({
      movement: null,
      sets: "",
      reps: "",
      time: "",
      rest: 0,
      instructions: "",
    });

    // Filter movements by query and filters
    const filteredMovements = movements.filter((m) => {
      const matchesQuery = m.name.toLowerCase().includes(movementQuery.toLowerCase());
      const typeAllowed = filters[m.type];
      return matchesQuery && typeAllowed;
    });

    // Filter muscles for muscle encyclopedia
    const filteredMuscles = muscles.filter((m) => {
      const matchesQuery = m.name.toLowerCase().includes(muscleQuery.toLowerCase());
      const group = m.muscle_group?.toLowerCase();
      return matchesQuery && muscleGroupFilters[group];
    });

    const handleExerciseChange = (e) => {
      const { name, value } = e.target;
      setCurrentExercise((prev) => ({ ...prev, [name]: value }));
    };

    const handleMovementSelect = (movementId) => {
      setCurrentExercise((prev) => ({ ...prev, movement: movementId }));
    };

    const addExercise = () => {
      if (!currentExercise.movement) {
        alert("Please select a movement.");
        return;
      }
      // Validate at least one of sets/reps/time filled (optional)
      if (!currentExercise.sets && !currentExercise.reps && !currentExercise.time) {
        alert("Please fill at least one of sets, reps, or time.");
        return;
      }

      setExercises((prev) => [
        ...prev,
        {
          ...currentExercise,
          sets: currentExercise.sets ? parseInt(currentExercise.sets) : null,
          reps: currentExercise.reps ? parseInt(currentExercise.reps) : null,
          time: currentExercise.time ? parseInt(currentExercise.time) : null,
          rest: currentExercise.rest ? parseInt(currentExercise.rest) : 0,
          routine_id: routineId,
        },
      ]);

      // Reset form for next exercise
      setCurrentExercise({
        movement: null,
        sets: "",
        reps: "",
        time: "",
        rest: 0,
        instructions: "",
      });
      setMovementQuery("");
    };

    const handleSubmitAll = async () => {
      if (exercises.length === 0) {
        alert("Add at least one exercise.");
        return;
      }
      try {
        // TODO: POST workout metadata & exercises to your backend or supabase here
        // Example:
        // await api.post('/routines', workoutData);
        // await api.post('/exercises', exercises);
        alert("Workout and exercises saved successfully!");
        onFinish(routineId, exercises);
      } catch (error) {
        console.error("Error saving workout", error);
        alert("Error saving workout");
      }
    };

    return (
      <div style={{ maxWidth: "1200px", margin: "2rem auto" }}>
        <h2>Step 2: Add Exercises</h2>

        {/* Exercise Entry Form */}
        <div
          style={{
            display: "flex",
            gap: "1rem",
            marginBottom: "2rem",
            flexWrap: "wrap",
            backgroundColor: "white",
            padding: "1rem",
            borderRadius: "10px",
            border: "1px solid #ccc",
          }}
        >
          {/* Movement selector + search */}
          <div style={{ flex: 1, minWidth: "280px" }}>
            <input
              type="text"
              placeholder="Search movement..."
              value={movementQuery}
              onChange={(e) => setMovementQuery(e.target.value)}
              style={{ width: "100%", padding: "0.5rem", marginBottom: "0.5rem" }}
            />
            <div
              style={{
                maxHeight: "200px",
                overflowY: "auto",
                border: "1px solid #ddd",
                borderRadius: "6px",
                padding: "0.25rem",
                backgroundColor: "#fafafa",
              }}
            >
              {filteredMovements.length === 0 && <p>No movements found.</p>}
              {filteredMovements.map((m) => (
                <div
                  key={m.id}
                  onClick={() => handleMovementSelect(m.id)}
                  style={{
                    padding: "0.3rem 0.5rem",
                    cursor: "pointer",
                    backgroundColor: currentExercise.movement === m.id ? "#007bff" : "transparent",
                    color: currentExercise.movement === m.id ? "white" : "black",
                    borderRadius: "4px",
                    marginBottom: "0.2rem",
                  }}
                  title={m.name}
                >
                  {m.name}
                </div>
              ))}
            </div>
          </div>

          {/* Exercise detail inputs */}
          <div style={{ flex: 1, minWidth: "280px" }}>
            <label>
              Sets:
              <input
                type="number"
                name="sets"
                min="0"
                value={currentExercise.sets}
                onChange={handleExerciseChange}
                style={{ width: "100%", padding: "0.3rem", marginBottom: "0.5rem" }}
              />
            </label>
            <label>
              Reps:
              <input
                type="number"
                name="reps"
                min="0"
                value={currentExercise.reps}
                onChange={handleExerciseChange}
                style={{ width: "100%", padding: "0.3rem", marginBottom: "0.5rem" }}
              />
            </label>
            <label>
              Time (seconds):
              <input
                type="number"
                name="time"
                min="0"
                value={currentExercise.time}
                onChange={handleExerciseChange}
                style={{ width: "100%", padding: "0.3rem", marginBottom: "0.5rem" }}
              />
            </label>
            <label>
              Rest (seconds):
              <input
                type="number"
                name="rest"
                min="0"
                value={currentExercise.rest}
                onChange={handleExerciseChange}
                style={{ width: "100%", padding: "0.3rem", marginBottom: "0.5rem" }}
              />
            </label>
            <label>
              Instructions:
              <textarea
                name="instructions"
                value={currentExercise.instructions}
                onChange={handleExerciseChange}
                style={{ width: "100%", minHeight: "80px", padding: "0.3rem" }}
              />
            </label>

            <button
              onClick={addExercise}
              style={{
                marginTop: "1rem",
                padding: "0.5rem 1rem",
                backgroundColor: "#007bff",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                fontWeight: "600",
              }}
            >
              Add Exercise
            </button>
          </div>
        </div>

        {/* List of added exercises */}
        <div
          style={{
            marginBottom: "2rem",
            backgroundColor: "white",
            borderRadius: "10px",
            padding: "1rem",
            border: "1px solid #ccc",
            maxHeight: "250px",
            overflowY: "auto",
          }}
        >
          <h3>Exercises Added</h3>
          {exercises.length === 0 ? (
            <p>No exercises added yet.</p>
          ) : (
            <ul>
              {exercises.map((ex, i) => {
                const movementName = movements.find((m) => m.id === ex.movement)?.name || "Unknown";
                return (
                  <li key={i} style={{ marginBottom: "0.5rem" }}>
                    <strong>{movementName}</strong> — Sets: {ex.sets ?? "-"}, Reps: {ex.reps ?? "-"}, Time: {ex.time ?? "-"}s, Rest: {ex.rest}s
                    <br />
                    Instructions: {ex.instructions || "None"}
                  </li>
                );
              })}
            </ul>
          )}
        </div>

        {/* Mini Encyclopedias side-by-side */}
        <div
          style={{
            display: "flex",
            gap: "2rem",
            flexWrap: "wrap",
            justifyContent: "center",
          }}
        >
          {/* Movements Encyclopedia */}
          <div
            style={{
              flex: "1 1 400px",
              backgroundColor: "white",
              padding: "1rem",
              borderRadius: "10px",
              border: "1px solid #ccc",
              maxHeight: "400px",
              overflowY: "auto",
            }}
          >
            <h3>Movement Encyclopedia</h3>
            <input
              type="text"
              placeholder="Search movements..."
              value={movementQuery}
              onChange={(e) => setMovementQuery(e.target.value)}
              style={{ width: "100%", padding: "0.5rem", marginBottom: "1rem" }}
            />
            {movements.length === 0 && <p>Loading movements...</p>}
            {movements.length > 0 &&
              movements
                .filter(
                  (m) =>
                    m.name.toLowerCase().includes(movementQuery.toLowerCase()) &&
                    filters[m.type]
                )
                .map((movement) => (
                  <div key={movement.id} style={{ marginBottom: "0.5rem" }}>
                    <strong>{movement.name}</strong> ({movement.type})
                  </div>
                ))}
          </div>

          {/* Muscles Encyclopedia */}
          <div
            style={{
              flex: "1 1 400px",
              backgroundColor: "white",
              padding: "1rem",
              borderRadius: "10px",
              border: "1px solid #ccc",
              maxHeight: "400px",
              overflowY: "auto",
            }}
          >
            <h3>Muscle Encyclopedia</h3>
            <input
              type="text"
              placeholder="Search muscles..."
              value={muscleQuery}
              onChange={(e) => setMuscleQuery(e.target.value)}
              style={{ width: "100%", padding: "0.5rem", marginBottom: "1rem" }}
            />

            {muscles.length === 0 && <p>Loading muscles...</p>}
            {muscles.length > 0 &&
              muscles
                .filter(
                  (m) =>
                    m.name.toLowerCase().includes(muscleQuery.toLowerCase()) &&
                    muscleGroupFilters[m.muscle_group?.toLowerCase()]
                )
                .map((muscle) => (
                  <div key={muscle.id} style={{ marginBottom: "0.5rem" }}>
                    <strong>{muscle.name}</strong> — Group: {muscle.muscle_group}
                  </div>
                ))}
          </div>
        </div>

        {/* Navigation Buttons */}
        <div
          style={{
            marginTop: "2rem",
            display: "flex",
            justifyContent: "space-between",
            maxWidth: "600px",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        >
          <button
            onClick={onBack}
            style={{
              padding: "0.75rem 1.5rem",
              backgroundColor: "#6c757d",
              color: "white",
              border: "none",
              borderRadius: "8px",
              fontSize: "1rem",
              cursor: "pointer",
            }}
            disabled={step === 1}
          >
            ← Back
          </button>

          <button
            onClick={handleSubmitAll}
            style={{
              padding: "0.75rem 1.5rem",
              backgroundColor: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "8px",
              fontSize: "1rem",
              cursor: "pointer",
            }}
          >
            Finish Workout
          </button>
        </div>
      </div>
    );
  };

  return (
    <>
      <NavBar />
      <div className="background" style={{ minHeight: "100vh", padding: "1rem" }}>
        <div className="title" style={{ marginBottom: "1rem", textAlign: "center" }}>
          Create Workout
        </div>

        {step === 1 && (
          <Step1 initialData={workoutData} onNext={(data) => {
            setWorkoutData(data);
            setStep(2);
          }} />
        )}
        {step === 2 && (
          <Step2
            routineId={workoutData.id}
            onBack={() => setStep(1)}
            onFinish={(routineId, exercises) => {
              // For now, just alert and reset
              alert(`Workout ${workoutData.name} created with ${exercises.length} exercises!`);
              setStep(1);
              setWorkoutData({ id: workoutData.id + 1, name: "", description: "" });
              setExercises([]);
            }}
          />
        )}
      </div>
    </>
  );
}
