import React, { useState, useEffect } from "react";
import axios from "axios";
import NavBar from "../components/NavBar.tsx";
import "./Encyclopedia.css";

export default function Encyclopedia() {
  const [movements, setMovements] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredMovements, setFilteredMovements] = useState([]);

  useEffect(() => {
    const fetchMovements = async () => {
      try {
        const response = await axios.get("/api/movements");
        setMovements(response.data);
        setFilteredMovements(response.data);
      } catch (error) {
        console.error("Error fetching movements:", error);
      }
    };

    fetchMovements();
  }, []);

  useEffect(() => {
    const filtered = movements.filter((movement) =>
      Object.values(movement).some((value) =>
        value.toString().toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
    setFilteredMovements(filtered);
  }, [searchQuery, movements]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  return (

    <div className="background">
      <NavBar />
      <div className="title">Encyclopedia</div>
      <div className="centerconsole">
      <input
        type="text"
        placeholder="Search movements..."
        value={searchQuery}
        onChange={handleSearchChange}
        style={{
          width: "90%",

          marginBottom: "20px",

          border: "1px solid #ccc",
        }}
      />
      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {filteredMovements.map((movement) => (
          <div
            key={movement.id}
            style={{
              border: "1px solid #ccc",
              borderRadius: "10px",

              width: "200px",
              textAlign: "center",
              boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
            }}
          >
            <h3>{movement.name}</h3>
            <p>Type: {movement.movement_type}</p>
            <p>Equipment: {movement.equipment}</p>
          </div>
        ))}
      </div>
      </div>
    </div>
  );
}
