import React, { useState, useEffect } from "react";
import axios from "axios";
import NavBar from "../components/NavBar.tsx";
import "./Encyclopedia.css";

export default function Encyclopedia() {
  const [movements, setMovements] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredMovements, setFilteredMovements] = useState([]);
  const [filters, setFilters] = useState({
    workout: true,
    stretch: true,
    trigger_point: true,
  });

  useEffect(() => {
    const fetchMovements = async () => {
      try {
        const response = await axios.get("/api/movements");
        setMovements(response.data);
        setFilteredMovements(response.data);
      } catch (error) {
        console.error("Error fetching movements:", error);
      }
    };
    fetchMovements();
  }, []);

  useEffect(() => {
    const filtered = movements.filter((movement) => {
      const matchesSearch = Object.values(movement).some((value) =>
        value.toString().toLowerCase().includes(searchQuery.toLowerCase())
      );

      const type = movement.movement_type?.toLowerCase();
      const matchesFilter =
        (type === "workout" && filters.workout) ||
        (type === "stretch" && filters.stretch) ||
        (type === "trigger_point" && filters.trigger_point);

      return matchesSearch && matchesFilter;
    });
    setFilteredMovements(filtered);
  }, [searchQuery, movements, filters]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleCheckboxChange = (type) => {
    setFilters((prev) => ({
      ...prev,
      [type]: !prev[type],
    }));
  };

  return (
    <div className="background">
      <NavBar />
      <div className="title">Encyclopedia</div>

      <div className="centerconsole">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search movements..."
            value={searchQuery}
            onChange={handleSearchChange}
            style={{
              width: "80%",
              padding: "10px",
              fontSize: "16px",

              border: "1px solid #ccc",
              borderRadius: "8px",
              marginBottom: "10px",
            }}
          />
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "20px",

            marginBottom: "30px",
          }}
        >
          <label>
            <input
              type="checkbox"
              checked={filters.workout}
              onChange={() => handleCheckboxChange("workout")}
            />
            Workouts
          </label>
          <label>
            <input
              type="checkbox"
              checked={filters.stretch}
              onChange={() => handleCheckboxChange("stretch")}
            />
            Stretches
          </label>
          <label>
            <input
              type="checkbox"
              checked={filters.trigger_point}
              onChange={() => handleCheckboxChange("trigger_point")}
            />
            Trigger Points
          </label>
        </div>

        <div style={{ display: "flex", flexWrap: "wrap", gap: "20px", justifyContent: "center" }}>
          {filteredMovements.map((movement) => (
            <div
              key={movement.id}
              style={{
                border: "1px solid #ccc",
                borderRadius: "10px",
                width: "200px",
                textAlign: "center",
                boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                padding: "10px",
              }}
            >
              <h3>{movement.name}</h3>
              <p>Type: {movement.movement_type}</p>
              <p>Equipment: {movement.equipment}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
