import React, { useState, useEffect } from "react";
import { getAllMovements } from "../../../server/api/movementsService.js";
import NavBar from "../components/NavBar.tsx";
import "./Encyclopedia.css";

export default function Encyclopedia() {
  const [movements, setMovements] = useState([]);
  const [query, setQuery] = useState("");
  const [filters, setFilters] = useState({
    workout: true,
    stretch: true,
    trigger_point: true,
  });




  useEffect(() => {
    const fetchMovements = async () => {
      try {
        const res = await axios.get("http://localhost:3001/api/movements"); 
        setMovements(res.data);
      } catch (err) {
        console.error("Error fetching movements:", err);
      }
    };
    fetchMovements();
  }, []);
  const filtered = movements.filter(m =>
    m.name.toLowerCase().includes(query.toLowerCase()) ||
    m.movement_muscle.some(mm =>
      mm.muscle.name.toLowerCase().includes(query.toLowerCase()) ||
      mm.muscle.muscle_group.toLowerCase().includes(query.toLowerCase())
    )
  );

  const handleCheckboxChange = (type) => {
    setFilters((prev) => ({
      ...prev,
      [type]: !prev[type],
    }));
  };

  return (
    <div className="background">
      <NavBar />
      <div className="title">Encyclopedia</div>

      <div className="centerconsole">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search movements..."
            value={query}
            onChange={e => setQuery(e.target.value)}
            style={{
              width: "80%",
              padding: "10px",
              fontSize: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              marginBottom: "10px",
            }}
          />
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "20px",

            marginBottom: "30px",
          }}
        >
          <label>
            <input
              type="checkbox"
              checked={filters.workout}
              onChange={() => handleCheckboxChange("workout")}
            />
            Workouts
          </label>
          <label>
            <input
              type="checkbox"
              checked={filters.stretch}
              onChange={() => handleCheckboxChange("stretch")}
            />
            Stretches
          </label>
          <label>
            <input
              type="checkbox"
              checked={filters.trigger_point}
              onChange={() => handleCheckboxChange("trigger_point")}
            />
            Trigger Points
          </label>
        </div>

        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "20px",
            justifyContent: "center",
          }}
        >
          {filtered.map((movement) => (
            <div
              key={movement.id}
              style={{
                border: "1px solid #ccc",
                borderRadius: "10px",
                width: "200px",
                textAlign: "center",
                boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                padding: "10px",
              }}
            >
              <h3>{movement.name}</h3>
              <p>Type: {movement.movement_type}</p>
              <p>Equipment: {movement.equipment}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
