import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { signup, login } from "../components/movementService.js";
import NavBar from "../components/NavBar.js";

export default function LoginPage() {
  const navigate = useNavigate();
  const [isSignup, setIsSignup] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [inviteCode, setInviteCode] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      if (isSignup) {
        if (!inviteCode.trim()) {
          setError("Invite code is required for signup.");
          setLoading(false);
          return;
        }
        const data = await signup(email, password, inviteCode);
        // You might want to auto-login or prompt user to login after signup
        alert("Signup successful! Please log in.");
        setIsSignup(false);
      } else {
        const data = await login(email, password);
        // Save user session info to localStorage/sessionStorage or context here
        localStorage.setItem("user", JSON.stringify(data.user));
        localStorage.setItem("session", JSON.stringify(data.session));
        navigate("/workouts");
      }
    } catch (err) {
      setError(err.response?.data?.error || err.message || "Unexpected error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="background">
      <NavBar />
      <h2 style={{ textAlign: "center" }}>{isSignup ? "Sign Up" : "Log In"}</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          autoComplete="email"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          autoComplete={isSignup ? "new-password" : "current-password"}
        />
        {isSignup && (
          <input
            type="text"
            placeholder="Invite Code"
            value={inviteCode}
            onChange={(e) => setInviteCode(e.target.value)}
            required={isSignup}
          />
        )}
        {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}
        <button type="submit" disabled={loading} style={{ padding: "0.75rem", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: 6, cursor: "pointer" }}>
          {loading ? "Please wait..." : isSignup ? "Sign Up" : "Log In"}
        </button>
      </form>
      <p style={{ marginTop: "1rem", textAlign: "center" }}>
        {isSignup ? "Already have an account? " : "Don't have an account? "}
        <button
          onClick={() => {
            setError(null);
            setIsSignup(!isSignup);
          }}
          style={{ color: "#007bff", background: "none", border: "none", cursor: "pointer", textDecoration: "underline", padding: 0 }}
        >
          {isSignup ? "Log In" : "Sign Up"}
        </button>
      </p>
    </div>
  );
}
