import React, { useState, useEffect } from "react";
import NavBar from "../components/NavBar.tsx";
import FlipCard from "../components/Flipcard.tsx";
import "./Encyclopedia.css";
import {
  getAllMovements,
  getAllMuscles,
} from "../components/movementService.js";
export default function Encyclopedia() {
  const [movements, setMovements] = useState([]);
  const [muscles, setMuscles] = useState([]);
  const [flipped, setFlipped] = useState(false);
  const [query, setQuery] = useState("");
  const [filters, setFilters] = useState({
    workout: true,
    stretch: true,
    trigger_point: true,
  });
  const workoutFilter = {
    workout: 3,
    stretch: 2,
    trigger_point: 1,
  };
  useEffect(() => {
    getAllMuscles()
      .then((data) => {
        const dict = data.reduce((acc, muscle) => {
          acc[muscle.id] = muscle.name;
          return acc;
        }, {});
        setMusclesDict(dict);
      })
      .catch(console.error);
    getAllMovements().then(setMovements).catch(console.error);
  }, []);
  console.log("Muscles:", muscles);
  useEffect(() => {
    setMovements((prev) => [...prev]);
  }, [flipped]);

  const filtered = movements
    .filter(
      (m) =>
        (m.name.toLowerCase().includes(query.toLowerCase()) ||
          m.movement_muscles.some((mm) =>
            mm.role.toLowerCase().includes(query.toLowerCase())
          )) &&
        m.type &&
        filters[m.type]
    )
    .sort((a, b) => {
      if (workoutFilter[a.type] < workoutFilter[b.type]) return 1;
      if (workoutFilter[a.type] > workoutFilter[b.type]) return -1;
      return 0;
    });

  const handleCheckboxChange = (type) => {
    setFilters((prev) => ({
      ...prev,
      [type]: !prev[type],
    }));
  };

  return (
    <div className="background">
      <NavBar />
      <div className="title">Encyclopedia</div>

      <div className="centerconsole">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search movements..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            style={{
              width: "80%",
              padding: "10px",
              fontSize: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              marginBottom: "10px",
            }}
          />

          <label style={{ marginTop: "10px" }}>
            <input
              type="checkbox"
              checked={flipped}
              onChange={() => setFlipped(!flipped)}
            />{" "}
            Flip
          </label>
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "20px",

            marginBottom: "30px",
          }}
        >
          <label>
            <input
              type="checkbox"
              checked={filters.workout}
              onChange={() => handleCheckboxChange("workout")}
            />
            Workouts
          </label>
          <label>
            <input
              type="checkbox"
              checked={filters.stretch}
              onChange={() => handleCheckboxChange("stretch")}
            />
            Stretches
          </label>
          <label>
            <input
              type="checkbox"
              checked={filters.trigger_point}
              onChange={() => handleCheckboxChange("trigger_point")}
            />
            Trigger Points
          </label>
        </div>

        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "20px",
            justifyContent: "center",
          }}
        >
          <div className="movement-grid">
            {filtered.map((movement) => (
              <FlipCard
                flippedState={flipped}
                key={movement.id}
                movement={movement}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
