import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import "./NavBar.css";

export default function NavBar({ color }) {
  const isMobile = window.innerWidth <= 768;
  const [showDropdown, setShowDropdown] = useState(false);
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  return (
    <div
      className="navcontainer"
    >
      <div
        className={`button`}
        role="button"
        aria-label="Toggle navigation"
        tabIndex={0}
      >
        <nav className="nav">
          <div className="navlinks">
            <NavLink
              to="/"
              className="navlink"
              style={{ animationDelay: "300ms" }}
            >
              {isMobile ? "üè†" : "Home"}
            </NavLink>
            <div
              className="navlink dropdown"
              style={{ animationDelay: "400ms" }}
              onMouseEnter={() => setShowDropdown(true)}
              onMouseLeave={() => setShowDropdown(false)}
            >
              {isMobile ? "üìö" : "Encyclopedia ‚ñæ"}
              {showDropdown && (
                <div className="dropdown-menu">
                  <NavLink to="/encyclopedia/movements" className="dropdown-item">
                    Movement Encyclopedia
                  </NavLink>
                  <NavLink to="/encyclopedia/muscles" className="dropdown-item">
                    Muscle Encyclopedia
                  </NavLink>
                </div>
              )}
            </div>
            <NavLink
              to="/workouts"
              className="navlink"
              style={{ animationDelay: "500ms" }}
            >
              {isMobile ? "üìãüèãÔ∏è" : "Workouts"}
            </NavLink>
            <NavLink
              to="/about"
              className="navlink"
              style={{ animationDelay: "600ms" }}
            >
              {isMobile ? "üìÑ" : "About"}
            </NavLink>
          </div>
        </nav>
      </div>
    </div>
  );
}
