import React, { useState, useEffect } from "react";
import "./Flipcard.css";
export default function FlipCard({ flippedState, movement }) {
  const [flipped, setFlipped] = useState(false);
  const [expanded, setExpanded] = useState(false);
  const [visable, setVisable] = useState(false);
  useEffect(() => {
    setFlipped(flippedState);
  }, [flippedState]);

  return (
    <div className="movement-card-container">
      <div
        className={`movement-card ${flipped ? "flipped" : ""}`}
        onClick={() => setFlipped(!flipped)}
      >
        <div className="movement-card-expansion" onMouseEn={() => setVisable(true)} onMouseLeave={() => setVisable(false)}>
          {(expanded || visable) && (
            <button
              className="expand-button"
              onClick={(e) => {
                e.stopPropagation();
                setExpanded(!expanded);
              }}
            >
              {expanded ? "⇦⇨" : "⇨⇦"}
            </button>
          )}
        </div>
        <div className="movement-card-front">
          <img
            src={`/anatomy/${movement.name
              .replace(/\s*[-–]\s*/g, "-")
              .replace(/\s+/g, "_")}.jpg`}
            alt={movement.name}
            style={{
              width: "100%",
              height: "60%",
              borderRadius: "12px 12px 0 0",
              objectFit: "cover",
            }}
          />

          <div className="cardTitle">{movement.name}</div>
        </div>

        <div className="movement-card-back">
          <div className="cardTitle">{movement.name}</div>
          <p>
            <strong>Type:</strong> {movement.type}
          </p>
          <p>
            <strong>Equipment:</strong> {movement.equipment || "None"}
          </p>
        </div>
      </div>
    </div>
  );
}
