import React, { useState, useEffect } from "react";
import NavBar from "../components/NavBar.tsx";
import axios from "axios";
import "./Exercise.css";
import { getRoutineNums } from "../components/movementService.js";

export default function CreateWorkout() {
  const [step, setStep] = useState(1);
  const [workoutData, setWorkoutData] = useState({
    id: null,
    name: "",
    description: "",
  });

  useEffect(() => {
    async function fetchIds() {
      try {
        const res = getRoutineNums();
        const ids = res.data.map((r) => r.id);
        const newId = ids.length ? Math.max(...ids) + 1 : 1;
        setWorkoutData((prev) => ({ ...prev, id: newId }));
      } catch (err) {
        console.error("Error fetching routines", err);
      }
    }
    fetchIds();
  }, []);

  const handleNext = () => setStep((prev) => prev + 1);
  const handlePrev = () => setStep((prev) => prev - 1);

  const handleChange = (e) => {
    setWorkoutData({ ...workoutData, [e.target.name]: e.target.value });
  };

  return (
    <div className="background">
      <NavBar />
      <div style={{ marginTop: "2rem" }}></div>
      <div className="title">Create Workout</div>

      <div className="centerconsole">
        <div className="main-content scrolling">
          {step === 1 && (
            <div
              style={{
                backgroundColor: "white",
                border: "1.5px solid rgb(183, 183, 184)",
                borderRadius: "10px",
                padding: "2rem",
                maxWidth: "500px",
                margin: "0 auto",
                textAlign: "center",
              }}
            >
              <h2 style={{ marginBottom: "1rem" }}>Step 1: Workout Details</h2>

              <div style={{ marginBottom: "1rem", textAlign: "left" }}>
                <label style={{ display: "block", fontWeight: 600 }}>
                  Workout Name:
                </label>
                <input
                  type="text"
                  name="name"
                  value={workoutData.name}
                  onChange={handleChange}
                  style={{
                    width: "100%",
                    padding: "0.5rem",
                    border: "1px solid #ccc",
                    borderRadius: "6px",
                  }}
                />
              </div>

              <div style={{ marginBottom: "1rem", textAlign: "left" }}>
                <label style={{ display: "block", fontWeight: 600 }}>
                  Description:
                </label>
                <textarea
                  name="description"
                  value={workoutData.description}
                  onChange={handleChange}
                  style={{
                    width: "100%",
                    padding: "0.5rem",
                    border: "1px solid #ccc",
                    borderRadius: "6px",
                    minHeight: "80px",
                  }}
                />
              </div>

              <p style={{ marginBottom: "1rem" }}>
                Generated ID: <strong>{workoutData.id}</strong>
              </p>

              <button
                onClick={handleNext}
                className="section"
                style={{
                  width: "fit-content",
                  padding: "0.5rem 1rem",
                  backgroundColor: "#28a745",
                  color: "white",
                  borderRadius: "8px",
                  fontSize: "1rem",
                }}
              >
                Next →
              </button>
            </div>
          )}

          {step > 1 && (
            <div style={{ textAlign: "center", marginTop: "1rem" }}>
              <button
                onClick={handlePrev}
                className="section"
                style={{
                  width: "fit-content",
                  padding: "0.5rem 1rem",
                  backgroundColor: "#6c757d",
                  color: "white",
                  borderRadius: "8px",
                  fontSize: "1rem",
                }}
              >
                ← Back
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
