import React, { useState, useEffect } from "react";
import "./Flipcard.css";
export default function FlipCard({ flippedState, movement }) {
  const [flipped, setFlipped] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    setFlipped(flippedState);
  }, [flippedState]);

  return (<>
    <div className="movement-card-container">
      <div
        className={`movement-card ${flipped ? "flipped" : ""}`}
        onClick={() => setFlipped(!flipped)}
      >
        <button
          className="overlay-button"
          onClick={(e) => {
            e.stopPropagation();
            setModalOpen(true);
          }}
          aria-label="Show full exercise details"
        >
          ⓘ
        </button>

        <div className="movement-card-front">
          <img
            src={`/anatomy/${movement.name
              .replace(/\s*[-–]\s*/g, "-")
              .replace(/\s+/g, "_")}.jpg`}
            alt={movement.name}
            style={{
              width: "100%",
              height: "60%",
              borderRadius: "12px 12px 0 0",
              objectFit: "cover",
            }}
          />

          <div className="cardTitle">{movement.name}</div>
        </div>

        <div className="movement-card-back">
          <div className="cardTitle">{movement.name}</div>
          <p>
            <strong>Type:</strong> {movement.type}
          </p>
          <p>
            <strong>Equipment:</strong> {movement.equipment || "None"}
          </p>
        </div>
      </div>

    </div>{modalOpen && (
        <div
          className="modal-overlay"
          onClick={() => setModalOpen(false)}
          role="dialog"
          aria-modal="true"
        >
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button
              className="modal-close"
              onClick={() => setModalOpen(false)}
              aria-label="Close modal"
            >
              ✕
            </button>
            <h2>{movement.name}</h2>
            <img
              src={`/anatomy/${movement.name
                .replace(/\s*[-–]\s*/g, "-")
                .replace(/\s+/g, "_")}.jpg`}
              alt={movement.name}
              className="modal-image"
            />
            <p>
              <strong>Type:</strong> {movement.type}
            </p>
            <p>
              <strong>Equipment:</strong> {movement.equipment || "None"}
            </p>

          </div>
        </div>
      )}</>
  );
}
