        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "20px",
            justifyContent: "center",
          }}
        >
          <div className="movement-grid">
            {filtered.map((movement) => (
              <FlipCard
                flippedState={flipped}
                key={movement.id}
                movement={movement}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
