import React, { useState, useEffect } from "react";
import "./Flipcard.css"; 
export default function FlipCard({ flippedState, movement }) {
    const [flipped, setFlipped] = useState(false);


    useEffect(() => {
      setFlipped(flippedState);
    }, [flippedState]);

    return (
      <div className="movement-card-container">
        <div
          className={`movement-card ${flipped ? "flipped" : ""}`}
          onClick={() => setFlipped(!flipped)}
        >

          <div className="movement-card-front">
            <img
              src={`/images/${movement.name.replace(/\s+/g, "_")}.jpg`}
              alt={movement.name}
              style={{
                width: "100%",
                height: "60%",
                borderRadius: "12px 12px 0 0",
                objectFit: "cover",
              }}
            />
            <h3>{movement.name}</h3>
          </div>


          <div className="movement-card-back">
            <h3>{movement.name}</h3>
            <p>
              <strong>Type:</strong> {movement.type}
            </p>
            <p>
              <strong>Equipment:</strong> {movement.equipment || "None"}
            </p>


          </div>
        </div>
      </div>
    );
  }
