import React, { useState, useEffect } from "react";
import NavBar from "../components/NavBar.tsx";
import axios from "axios"; // Assuming you have API endpoints to fetch routines

export default function CreateWorkout() {
  const [step, setStep] = useState(1);
  const [workoutData, setWorkoutData] = useState({
    id: null,
    name: "",
    description: "",
  });

  // Fetch current routine IDs to generate a new bigint ID
  useEffect(() => {
    async function fetchIds() {
      try {
        const res = await axios.get("/api/routines"); // Replace with your endpoint
        const ids = res.data.map((r) => r.id);
        const newId = ids.length ? Math.max(...ids) + 1 : 1;
        setWorkoutData((prev) => ({ ...prev, id: newId }));
      } catch (err) {
        console.error("Error fetching routines", err);
      }
    }
    fetchIds();
  }, []);

  const handleNext = () => setStep((prev) => prev + 1);
  const handlePrev = () => setStep((prev) => prev - 1);

  const handleChange = (e) => {
    setWorkoutData({ ...workoutData, [e.target.name]: e.target.value });
  };

  return (
    <div className="background">
      <NavBar />
      <div className="title">Create Workout</div>

      <div className="centerconsole">
        {step === 1 && (
          <div className="form-step">
            <h2>Step 1: Workout Details</h2>
            <label>
              Workout Name:
              <input
                type="text"
                name="name"
                value={workoutData.name}
                onChange={handleChange}
              />
            </label>

            <label>
              Description:
              <textarea
                name="description"
                value={workoutData.description}
                onChange={handleChange}
              />
            </label>

            <p>
              Generated ID: <strong>{workoutData.id}</strong>
            </p>

            <button onClick={handleNext}>Next →</button>
          </div>
        )}

        {step > 1 && (
          <div>
            <button onClick={handlePrev}>← Back</button>
          </div>
        )}
      </div>
    </div>
  );
}
